new Vue({el:"#blog",created(){this.query_api()},data:{posts:[]},methods:{query_api(){axios.get("https://admin.insights.ubuntu.com/wp-json/wp/v2/posts?per_page=3&page=1&_embed=True").then(response=>{this.posts=response.data.map(post=>({title:post.title.rendered,link:post.link,thumbnail:{image:post._embedded["wp:featuredmedia"][0].source_url,alt_text:post._embedded["wp:featuredmedia"][0].alt_text},date:moment(post.date).format("DD MMMM YYYY"),author:{name:post._embedded.author[0].name,link:post._embedded.author[0].link}}))})}}});